DROP TABLE IF EXISTS profile;
CREATE TABLE profile (userhash INTEGER PRIMARY KEY, username VARCHAR(255), email VARCHAR(255), pass VARCHAR(255), rol VARCHAR(255), urlAddress VARCHAR(255));
CREATE TABLE notes (id INTEGER PRIMARY KEY, userhash VARCHAR(255), note VARCHAR(2000), colour VARCHAR(255), date VARCHAR(255));
CREATE TABLE usernotesmapping (userhash VARCHAR(255) PRIMARY KEY, noteids VARCHAR(1000));